import React from 'react';

function Summary(props) {
  const sumData = ('data' in props && 'summaries' in props.data) ? props.data.summaries : [];

  const summaries = sumData.map((cv, i) => {
    return (
      <div className='list-group-item p-2' key={i}>
        <div className='list-group-item-title p-1 pb-0 m-0'>{cv.title}</div>
        <div className='list-group-item-text p-1 pl-4 pt-0 m-0'>{cv.text}</div>
      </div>
    );
  });

  return (
    <div className="card" id="summary">
      <div className="card-header">Summary</div>
      <div className="card-body list-group list-group-flush p-0">
        {summaries}
      </div>
    </div>
  );
}

class Resume extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: {}
    }
  }

  componentDidMount() {
    const fixedRef = this; //creating reference for .then async calls

    fetch("./resumeData.json")
      .then((resp) => resp.json()) // Transform the data into json
      .then(function(data) {
        fixedRef.setState({'data':data});
      });
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-4 col-xs-5">

          <Summary data={this.state.data} />

            <div className="panel panel-default" id="education">
              <div className="panel-heading">
                <h2 className='panel-title'>Education</h2>
              </div>
              <dl className="panel-body list-group">
                <div className='list-group-item'>
                  <dt className='list-group-item-heading'>University of Michigan</dt>
                  <dd className='list-group-item-text'>BA in Russian and Eastern European Studies (2000-2004)</dd>
                </div>
              </dl>
            </div>

            <div className="panel panel-default" id="languages">
              <div className="panel-heading">
                <h2 className='panel-title'>Languages</h2>
              </div>
              <ul className="panel-body list-unstyled">
              </ul>
            </div>

            <div className="panel panel-default" id="skills">
              <div className="panel-heading">
                <h2 className='panel-title'>Development Skills</h2>
              </div>
              <ul className="panel-body list-unstyled">
              </ul>
            </div>

          </div>



          <div className="col-md-8 col-xs-7">
            <div className="panel panel-default" id="experiences">
              <div className="panel-heading">
                <h2 className='panel-title'>Experience</h2>
              </div>
              <div className="panel-body"></div>
            </div>
          </div>
        </div>

      </div>

    );
  }
}

export default Resume;
